import React, { Component } from "react";
import AddFishForm from "./addFishForm";

class Inventory extends Component {
  handleChange = (e, key) => {
    const fish = this.props.fishes[key];
    const updatedFish = { ...fish };
    console.log(updatedFish[e.target.value], key);
  };

  renderInventory = key => {
    const { fishes } = this.props;
    const fish = fishes[key];
    console.log(fish);
    return (
      <div className="fish-edit" key={key}>
        <input
          name="name"
          type="text"
          placeholder="Fish Name"
          value={fish.name}
          onChange={e => this.handleChange(e, key)}
        />
        <input
          name="price"
          type="text"
          placeholder="Fish Price"
          value={fish.price}
        />
        <select type="text" name="status" value={fish.status}>
          <option value="available">Fresh!</option>
          <option value="unavailable">Sold Out!</option>
        </select>
        <textarea name="desc" value={fish.desc} placeholder="Fish Desc" />
        <input
          name="image"
          type="text"
          placeholder="Fish Image"
          value={fish.image}
        />
      </div>
    );
  };
  render() {
    const { fishes, addFish, loadSampleFish } = this.props;
    return (
      <div>
        <h2>Inventory</h2>
        {Object.keys(fishes).map(this.renderInventory)}
        <AddFishForm addFish={addFish} />
        <button onClick={loadSampleFish}>Load Sample Fishes</button>
      </div>
    );
  }
}

export default Inventory;
