import React, { Component } from "react";
import AddFishForm from "./addFishForm";
import PropTypes from "prop-types";
class Inventory extends Component {
  handleChange = (e, key) => {
    const fish = this.props.fishes[key];
    const updatedFish = { ...fish, [e.target.name]: e.target.value };

    this.props.updateFish(key, updatedFish);
  };

  renderInventory = key => {
    const { fishes } = this.props;
    const fish = fishes[key];

    return (
      <div className="fish-edit" key={key}>
        <input
          name="name"
          type="text"
          placeholder="Fish Name"
          value={fish.name}
          onChange={e => this.handleChange(e, key)}
        />
        <input
          name="price"
          type="text"
          placeholder="Fish Price"
          value={fish.price}
          onChange={e => this.handleChange(e, key)}
        />
        <select
          type="text"
          name="status"
          value={fish.status}
          onChange={e => this.handleChange(e, key)}
        >
          <option value="available">Fresh!</option>
          <option value="unavailable">Sold Out!</option>
        </select>
        <textarea
          name="desc"
          value={fish.desc}
          placeholder="Fish Desc"
          onChange={e => this.handleChange(e, key)}
        />
        <input
          name="image"
          type="text"
          placeholder="Fish Image"
          value={fish.image}
          onChange={e => this.handleChange(e, key)}
        />
        <button onClick={() => this.props.deleteFish(key)}>Remove Fish</button>
      </div>
    );
  };
  render() {
    const { fishes, addFish, loadSampleFish } = this.props;
    return (
      <div>
        <h2>Inventory</h2>
        {Object.keys(fishes).map(this.renderInventory)}
        <AddFishForm addFish={addFish} />
        <button onClick={loadSampleFish}>Load Sample Fishes</button>
      </div>
    );
  }
}
Inventory.propTypes = {
  fishes: PropTypes.object.isRequired,
  addFish: PropTypes.func.isRequired
};
export default Inventory;
